import React, { useState, useEffect, setState } from 'react';

function NotePopup(props){


    const [popupVisible,setPopupVisible] = useState('')
    const [popupConfig,setPopupConfig] = useState('')
    const [title,setTitle] = useState('')
    const [value,setValue] = useState('')
    const [noteText,setNoteText] = useState('')
    useEffect(() => {
        window.addEventListener('popup', function(e){
            var dataTitle = ''
            var dataValue = ''
            var eventData = e.detail.data.data
            if (eventData){
                if (eventData.clickedPoint.selectedAttributes.length > 0){
                    dataValue = eventData.clickedPoint.selectedAttributes[0].value
                    dataTitle = eventData.clickedPoint.selectedAttributes[0].column.name
                }
                if (eventData.clickedPoint.selectedMeasures > 0){
                    dataValue = eventData.clickedPoint.selectedMeasures[0].value
                    dataTitle = eventData.clickedPoint.selectedMeasures[0].column.name
                }
            }
            setTitle(dataTitle)
            setValue(dataValue);
            setPopupVisible(true);
        })
      }, [])
    function saveButton(){
        alert("Note saved: \n\n"+title+" - "+value+"\n\nNote - "+noteText)
        setPopupVisible(false)

    }
    function togglePopupVisible(){
        setPopupVisible(false)
    }
    var disp = popupVisible ? 'flex' : 'none';

    var style = {
        display:disp,
        position:'absolute',
        top:'35%',
        left:'35%',
        height:'350px',
        width:'300px',
        borderRadius:'5px',
        boxShadow:'0px 0px 150px #cccccc',
        flexDirection:'column',
        padding:'15px',
        background:'var(--primary-color)',
        boxShadow:'0px 0px 150px #efefef'
    }
    return (
        <div style={style}>
            <div style={{position:'absolute',marginLeft:'280px',marginTop:'5px'}} onClick={togglePopupVisible}><CloseIcon /></div>
            <div style={{marginTop:'5px',fontSize:'16px',fontWeight:'600'}}>{title}</div>
            <div style={{marginTop:'5px',fontSize:'14px',marginBottom:'10px'}}>{value}</div>
            <textarea style={{height:'250px'}} value={noteText} onChange={(e) => setNoteText(e.target.value)}></textarea>
            <div className="button" style={{fontSize:'14px',marginTop:'15px',height:'45px',width:'80px',display:'flex',justifyContent:'center',alignItems:'center',background:'var(--secondary-color)',color:'var(--primary-color)'}} onClick={saveButton}>Save</div>
        </div>
    )
}

function CloseIcon(){
    return <svg xmlns="http://www.w3.org/2000/svg" viewBox="-2 -2 24 24" width="24" fill="currentColor"><path d="M4 2a2 2 0 0 0-2 2v12a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V4a2 2 0 0 0-2-2H4zm0-2h12a4 4 0 0 1 4 4v12a4 4 0 0 1-4 4H4a4 4 0 0 1-4-4V4a4 4 0 0 1 4-4z"></path><path d="M11.414 10l2.829 2.828a1 1 0 0 1-1.415 1.415L10 11.414l-2.828 2.829a1 1 0 1 1-1.415-1.415L8.586 10 5.757 7.172a1 1 0 0 1 1.415-1.415L10 8.586l2.828-2.829a1 1 0 0 1 1.415 1.415L11.414 10z"></path></svg>;
  }
export default NotePopup;
