import logo from './logo.svg';
import './App.css';
import {
  BrowserRouter as Router,
  Routes,
  Switch,
  Route,
  Link,

} from "react-router-dom";
import Settings from './Settings'
import Content from './Content'
import React, { useState, useEffect, setState } from 'react';

const APP_VERSION = '1.0.0'


function App() {
  const [settings, setSettings] = useState('')
  const [settingsVisible, setSettingsVisible] = useState('')

  function applySettings(settings){
    setSettings(settings)
  }
  function saveSettings(settings){
    console.log(settings)
    var a = document.getElementById("saveButton");
    var file = new Blob([JSON.stringify(settings)], {type: 'json'});
    a.href = URL.createObjectURL(file);
    a.download = settings.name+APP_VERSION;
  }
  function closeSettings(settings){
    setSettingsVisible(false)
  }
  function showSettings(){
    setSettingsVisible(true);
  }

  const openSettings = async (event) => {
    const file = event.target.files[0]
    const json = await convertJSON(file)
    setSettings(json)
  }  
  const convertJSON = (file) => {
    return new Promise((resolve, reject) => {
      const fileReader = new FileReader();
      fileReader.readAsText(file)
      fileReader.onload = () => {
        resolve(JSON.parse(fileReader.result));
      }
      fileReader.onerror = (error) => {
        reject(error);
      }
    })
  }

  const popover = {
    position: 'absolute',
    zIndex: '2',
    top:50,
    left:300,
    width:'600px',
    height:'600px',
    background:'#ffffff',
    boxShadow: '0px 0px 250px #ededed',
    padding:'25px'
  }
  return(
    <div>
      <Content settings={settings} showSettings={showSettings} />
      { settingsVisible ? <div style={ popover }>
    <Settings openSettings={openSettings} applySettings={applySettings} saveSettings={saveSettings} settings={settings} closeSettings={closeSettings}/>  
  </div> : null }

    </div>


  );
}

export default App;
